import React from 'react'
import styled from 'styled-components'
import chanlogo from '../images/chan.jpg'
import bg from '../images/bg.jpg'

const Container=styled.div`
    padding: 7px;
`
const Image=styled.img`
    background-size: contain;
    background-repeat: repeat;
    background-position: center;
    background-attachment: fixed;
    height: 250px;
    width: 100%;
    
`
const Imgbox=styled.div`
  width: 100%;
  display: flex;
  justify-content: center;
  
`
const ChanImg=styled.img`
  border-radius: 50%;
  height: 7rem;
  width: 7rem;
  margin-top: -9vh;
  box-shadow: 0px 0px 10px 1px #f18787;
  transition: ease-in-out 0.4s;
  &:hover{
    transform: scale(1.07);
  }
`


const Details=styled.div`
  display: flex;
  gap: 0px;
  padding: 0%;
  flex-direction:column;
  justify-content:center;
  align-items: center;
`
const ChannelName=styled.h2`
margin: 0%;
text-shadow: 0px 0px 2px red;
font-family: Verdana, Geneva, Tahoma, sans-serif;
`
const Desc=styled.h5`
margin: 3px;
color: #e0dada;
`
const Hr=styled.hr`
  margin: 5px 0px;
  border: 0.5px solid ${({theme})=>theme.lines};
`;
const Categories=styled.h3`
  margin: 0%;
  margin-top: 7px;
`

export const Channel = () => {
  return (
    <Container>
        <Image src={bg} />
        <Imgbox>
          <ChanImg src={chanlogo}/>
        </Imgbox>
        <Details>
          <ChannelName>Chan Chan Man</ChannelName>
          <Desc>Hi I make videos.</Desc>
        </Details>
        <Categories>Uploads</Categories>
        <Hr/>
        <></>
    </Container>
  )
}
